import React, { useEffect, useRef, useState } from "react";
import "./AudioPlayer.css";
import song1 from "../music/song1.mp3"
import song2 from "../music/song2.mp3"
import song3 from "../music/song3.mp3"
import song4 from "../music/song4.mp3"
import song5 from "../music/song5.mp3"


function Audio_Player() {
    const songs = [{
        name: "Tum se hi",
        movie: "jab we met",
        artist: "Mohit Chauhan",
        img: "https://i.pinimg.com/236x/d6/25/00/d625008c5ba3b93a8d457d989df05ee0.jpg",
        src: song1
    },
    {
        name: "Kaise Hua",
        movie: "Kabir Singh",
        artist: "Vishal Mishra",
        img: "https://a10.gaanacdn.com/gn_img/song/lJvKa56KDV/vKa794R2WD/size_m_1560412400.jpg",
        src: song2
    },
    {
        name: "Ek Dil Ek Jaan",
        movie: "Padmaavat",
        artist: "Arjit Singh",
        img:"data:image/jpeg;base64,",
        src: song3
    },
    {
        name: "Tujhe Kitna Chahne Lage Ham",
        movie: "Kabir Singh",
        artist: "Arjit Singh",
        img:'data:image/jpeg;base64,',
        src: song4
    },
    {
        name: "TUm se ",
        movie: "Teri Bato me esa uljha jiya",
        artist: "Arjit Singh",
        img:"data:image/jpeg;base64,",
        src: song5
    },

    ]
   const audioRef = useRef(null);
  const [current, setCurrent] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  const togglePlay = () => {
    if (isPlaying) audioRef.current.pause();
    else audioRef.current.play();
    setIsPlaying(!isPlaying);
  };

  const nextSong = () => {
    setCurrent((prev) => (prev + 1) % songs.length);
    setIsPlaying(true);
  };

  const prevSong = () => {
    setCurrent((prev) => (prev - 1 + songs.length) % songs.length);
    setIsPlaying(true);
  };


  useEffect(() => {
    if (isPlaying) audioRef.current.play();
  }, [current]);

  return (
    <div className="container">
      <div className="audio-player p-5">

        <div className="track-info p-5">
          <img src={songs[current].img} className="track-image" alt="" />
          <div>
            <div className="marquee">
              <span className="h5 mb-1">{songs[current].name}</span>
            </div>
            <p className="text-muted mb-0">{songs[current].movie}</p>
            <p className="text-muted mb-0">{songs[current].artist}</p>
          </div>
        </div>

        <audio
          ref={audioRef}
          src={songs[current].src}
        />

        <div className="controls">
          <button className="control-button" onClick={prevSong}>
            <i className="fas fa-step-backward"></i>
          </button>

          <button className="play-button" onClick={togglePlay}>
            <i className={`fas ${isPlaying ? "fa-pause" : "fa-play"}`}></i>
          </button>

          <button className="control-button" onClick={nextSong}>
            <i className="fas fa-step-forward"></i>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Audio_Player;
